// -------------------------------// Kevin T. Manley// CSE 593// -------------------------------package rm;public class Flight extends ReservableItem{    public Flight( int flightNum, int flightSeats, int flightPrice ) {        this(Integer.toString(flightNum), flightSeats, flightPrice);    }    private Flight(String flightNum, int flightSeats, int flightPrice) {        super(flightNum, flightSeats, flightPrice);    }    public String getKey() {        return Flight.getKey( Integer.parseInt( getLocation() ) );    }    public static String getKey( int flightNum ) {        String s = "flight-" + flightNum;        return s.toLowerCase();    }    @Override    protected RMItem clone() {        return new Flight(getLocation(), getCount(), getPrice());    }    //		public static String getNumReservationsKey( int flightNum )//		{//			String s = "flight-" + flightNum + "-reservations";//			return s.toLowerCase();//		}}