// -------------------------------// Kevin T. Manley// CSE 593// -------------------------------package rm;import java.util.*;// A specialization of Hashtable with some//  extra diagnosticspublic class RMHashtable extends Hashtable<String,  RMItem> {	/**	 * Construct a new RM Hash table	 */    public RMHashtable() {      super();    }    /**     * Get table content as a string     * @return string     */	public String toString()    {        String s = "--- BEGIN RMHashtable ---\n";        for (Enumeration e = keys(); e.hasMoreElements(); ) {            Object key = e.nextElement();            RMItem value = get( key );            s = s + "[KEY='" + key + "']" + value.toString() + "\n";        }        s = s + "--- END RMHashtable ---";        return s;    }    public void dump()		{			System.out.println( toString() ); 		}}